import React from 'react'
import ReactDOM from 'react-dom'

/*
const Title = ({course}) => <h1>{course}</h1>

*/ 
const Title = (props) => {
  return  <h1>{props.course}</h1>
}

const Description = (props) => {
  return <p>{props.msg} {props.number}</p>
}

const Total = (props) => {
  let suma =0;
  props.numbers.array.forEach(element => {
    suma+= element;
  });
  return <p>{props.msg} {suma}</p>
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Title course={course}></Title>
      <Description msg = {part1} number = {exercises1} ></Description>
      <Description msg = {part2} number = {exercises2} ></Description>
      <Description msg = {part3} number = {exercises3} ></Description>
      <Total msg = {"Total of numbers"} numbers = {[exercises1 , exercises2 , exercises3]}>msg = Number of exercises</Total>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
